import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import AppIcon from '../images/icon.png';

//material ui
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const styles = {
    form: {
        textAlign: 'center'
    },
    image: {
        margin: '20px auto 10px auto',
        height: '50px',
        width: '50px'
    },
    pageTitle: {
        margin: '10px auto 20px auto'
    },
    textField: {
        margin: '10px auto 10px auto'
    },
    button: {
        marginTop: 20
    }
}

//login rendering
class login extends Component {
    //default fields
    constructor() {
        super();
        this.state ={
            email: '',
            password: '',
            loading: false, //loading after submit
            error: {}
        }
    }
    //redirect after submit
    handleSubmit = (event) => {
        console.log('hi');
    }
    //targets form field and allows change of target value
    handleChange= (event) => {
        this.setState({
           [event.target.name]: event.target.value
        })
    }
    render() {
            const { classes } = this.props;
        return (
            //create responsive division on page
            <Grid container className={classes.form}>
                <Grid item sm/>
                <Grid item sm>
                    <img src={AppIcon} alt="logo" className={classes.image}/>
                    <Typography variant="h4" className={classes.pageTitle}>
                        Login
                    </Typography>
                    {/*login form*/}
                    <form noValidate onSubmit={this.handleSubmit}>
                        <TextField id="email" name="email" type="email" label="Email"
                            className={classes.textField} value={this.state.email}
                            onChange={this.handleChange} fullWidth/>
                        <TextField id="password" name="password" type="password" label="password"
                            className={classes.textField} value={this.state.password}
                            onChange={this.handleChange} fullWidth/>
                        <Button type="submit" variant="contained" color="primary"
                            className={classes.button}>Login</Button>
                    </form>
                </Grid>
                <Grid item sm/>
            </Grid>
        );
    }
}

//checks prop types for login
login.propTypes = {
        classes: PropTypes.object.isRequired
}

export default withStyles(styles)(login);
